b3 = [-0.1691133677959442]

mean = [91.81403430301407, 84.86154882131669, 36.92420356026786]        # media del StandardScaler
scale = [5.740454253072095, 30.874583843871743, 1.459648607364617]      # desviación típica

# ==== 2. Funciones auxiliares ====
def standardize(x):
    return [(x[i] - mean[i]) / scale[i] for i in range(len(x))]

def relu(x):
    return [max(0, i) for i in x]

def sigmoid(x):
    return 1 / (1 + math.exp(-x))

def dot(W, x, b):
    """Multiplicación de matriz W (n_neuronas x n_entradas) * x + bias"""
    result = []
    for i in range(len(W[0])):  # para cada neurona
        s = 0
        for j in range(len(x)):
            s += x[j] * W[j][i]
        s += b[i]
        result.append(s)
    return result

# ==== 3. Función de inferencia ====
def predict(features):
    # Estandarizar entrada
    x = standardize(features)

    # Capa 1
    z1 = dot(W1, x, b1)
    a1 = relu(z1)

    # Capa 2
    z2 = dot(W2, a1, b2)
    a2 = relu(z2)

    # Capa 3 (salida)
    z3 = 0
    for i in range(len(a2)):
        z3 += a2[i] * W3[i][0]
    z3 += b3[0]
    y = sigmoid(z3)

    # Umbral de clasificación
    return 1 if y > 0.5 else 0, y
